package second;

public class Op3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//p88
		int a = 10,b=20;
		//대입연산자==
		System.out.println(a==b);//false
		System.out.println(a!=b);//true
		System.out.println(a>b);//false
		System.out.println(a>=b);//false
		System.out.println(a<b);//true
		System.out.println(a<=b);//true
		
		//and or 연산자
		System.out.println(a==10 || b==20);//true
		System.out.println(a==10 && b==20);//true
		System.out.println(a==10 & b==20);//true
		System.out.println(a==10 | b==20);//true
		//& |하나보다 || && 두개가 처리속도가 빠르다. 

		
		//p102
			}

}
